    static request({ url, data, callback, method = 'GET' }) {
        //wx.request
        wx.request({
            url: `${config.apiBaseUrl}${url}`,
            data,
            method,
            header: {
                appkey: config.appkey
            },
            success(res) {
                callback(res.data);
